CREATE OR REPLACE FUNCTION med_obter_desc_plano (nr_seq_plano_p bigint) RETURNS varchar AS $body$
DECLARE


ds_convenio_w		varchar(255);
ds_plano_w		varchar(100);


BEGIN


select	ds_plano,
	substr(obter_nome_convenio(cd_convenio),1,100)
into STRICT	ds_plano_w,
	ds_convenio_w
from	med_plano
where	nr_sequencia	= nr_seq_plano_p;


return	ds_convenio_w || ' - ' || ds_plano_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
