CREATE OR REPLACE FUNCTION med_obter_guia_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_guia_w	varchar(20);
nr_sequencia_w	bigint;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(coalesce(nr_sequencia,0))
	into STRICT	nr_sequencia_w
	from	med_faturamento
	where	nr_atendimento = nr_atendimento_p
	and	(cd_guia IS NOT NULL AND cd_guia::text <> '');

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		select	cd_guia
		into STRICT	cd_guia_w
		from	med_faturamento
		where	nr_sequencia = nr_sequencia_w;
	end if;
end if;

return cd_guia_w;

end;
