CREATE OR REPLACE FUNCTION med_obter_guia_ult_atend_cons (nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE


nr_atend_cons_w	bigint;
nr_seq_guia_w		bigint;
cd_guia_cons_w	varchar(20) := '';


BEGIN
if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then
	/* obter Ãºltima consulta */

	select	coalesce(max(a.nr_atendimento),0)
	into STRICT	nr_atend_cons_w
	from	med_classif_agenda c,
		agenda_consulta b,
		med_atendimento a
	where	c.nr_sequencia = b.nr_seq_classif_med
	and	b.nr_sequencia = a.nr_seq_agenda
	and	a.nr_seq_cliente = nr_seq_cliente_p
	and	c.ie_tipo = 'C';

	/* obter se guia consulta */

	if (nr_atend_cons_w > 0) then
		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_guia_w
		from	med_faturamento
		where	nr_atendimento = nr_atend_cons_w
		and	(cd_guia IS NOT NULL AND cd_guia::text <> '');

		/* obter guia consulta */

		if (nr_seq_guia_w > 0) then
			select	max(cd_guia)
			into STRICT	cd_guia_cons_w
			from	med_faturamento
			where	nr_sequencia = nr_seq_guia_w;
		end if;
	end if;
end if;

return cd_guia_cons_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
