CREATE OR REPLACE FUNCTION med_obter_pac_cid (nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE


cd_doenca_cid_w		varchar(010);


BEGIN


if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then

	select	max(a.cd_doenca_cid)
	into STRICT	cd_doenca_cid_w
	from	med_pac_cid a
	where	a.nr_seq_cliente	= nr_seq_cliente_p
