	select 	count(*) 
	into STRICT	qt_reg_w 
	from	med_clinica_avaliacao 
	where	nr_seq_tipo_aval = nr_seq_tipo_aval_p;
	 
	if (qt_reg_w	> 0) then 
		 
		select	count(*) 
		into STRICT	qt_reg_w 
		from	med_clinica_avaliacao 
		where	nr_seq_tipo_aval = nr_seq_tipo_aval_p 
		and		ie_clinica	= ie_clinica_p;
		 
		if (qt_reg_w	= 0) then 
			return 'N';
		end if;
		 
	end if;
	 
end if;
 
return 'S';
 
end;
