qt_reg_w	bigint;
cd_perfil_w	bigint;

BEGIN
cd_perfil_w	:= coalesce(obter_perfil_ativo,0);
 
select	count(*) 
into STRICT	qt_reg_w 
from	med_protocolo_exame_regra 
where	NR_SEQ_PROTOCOLO	= nr_sequencia_p;
 
if (qt_reg_w	> 0) then 
	select	count(*) 
	into STRICT	qt_reg_w 
	from	med_protocolo_exame_regra 
	where	NR_SEQ_PROTOCOLO	= nr_sequencia_p 
	and	((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(cd_pessoa_fisica_p,cd_especialidade)	= 'S')) 
	and	coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w 
	and	((coalesce(NM_USUARIO_PARAM::text, '') = '') or (NM_USUARIO_PARAM = obter_usuario_ativo));
	 
	if (qt_reg_w	= 0) then 
		return	'N';
	end if;
end if;
 
return	'S';
 
end;
