CREATE OR REPLACE FUNCTION med_obter_texto_padrao_item ( nr_seq_item_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE



nr_sequencia_w	bigint;


BEGIN

if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '')	then

	select	max(coalesce(nr_sequencia,0))
	into STRICT	nr_sequencia_w
	from	med_texto_padrao
	where	nr_seq_item_pront = nr_seq_item_p
	and	cd_medico	  = cd_medico_p
	and	ie_opcao_padrao = 'S';

end if;



return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
