CREATE OR REPLACE FUNCTION med_obter_valor_parametro ( nr_sequencia_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE


vl_parametro_w	varchar(255);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then
	begin
	select	max(a.vl_parametro)
	into STRICT	vl_parametro_w
	from	med_parametro_medico a
	where	a.cd_medico 		= cd_medico_p
	and	a.nr_seq_parametro 	= nr_sequencia_p;
	if (coalesce(vl_parametro_w::text, '') = '') then
		select 	max(a.vl_padrao)
		into STRICT	vl_parametro_w
		from 	med_parametro a
		where 	a.nr_sequencia     = nr_sequencia_p
		and 	not exists (	SELECT 	1
					from 	med_parametro_medico x
					where 	x.nr_seq_parametro 	= a.nr_sequencia
					and 	x.cd_medico 		= cd_medico_p);
	end if;
	end;
end if;

return vl_parametro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
