SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION med_valida_duplicar ( ie_tipo_texto_p bigint, nr_seq_cliente_p bigint) RETURNS varchar AS $body$
DECLARE

/* 	1 - Evolução
	3 - Receita
	4 - Pedido Exames
	6 - Texto Adicional
	8 - Atestado
	10 - Parecer Médico */
qtd_w		bigint  := 0;
retorno_w		varchar(1)  := 'N';


BEGIN
if (ie_tipo_texto_p = 1) then
	select 	count(*)
	into STRICT	qtd_w
	from 	med_evolucao
	where 	to_date(clock_timestamp(),'dd/mm/yyyy') 	= to_date(dt_evolucao,'dd/mm/yyyy')
	and	nr_seq_cliente 			= nr_seq_cliente_p;
elsif (ie_tipo_texto_p = 3) then
	select 	count(*)
	into STRICT	qtd_w
	from 	med_receita
	where 	to_date(clock_timestamp(),'dd/mm/yyyy') 	= to_date(dt_receita,'dd/mm/yyyy')
	and	nr_seq_cliente 			= nr_seq_cliente_p;
elsif (ie_tipo_texto_p = 4) then
	select 	count(*)
	into STRICT	qtd_w
	from 	med_pedido_exame
	where 	to_date(clock_timestamp(),'dd/mm/yyyy') 	= to_date(dt_solicitacao,'dd/mm/yyyy')
	and	nr_seq_cliente 			= nr_seq_cliente_p;
elsif (ie_tipo_texto_p = 6) then
	select 	count(*)
	into STRICT	qtd_w
	from 	med_texto_adicional
	where 	to_date(clock_timestamp(),'dd/mm/yyyy') 	= to_date(dt_texto,'dd/mm/yyyy')
	and	nr_seq_cliente 			= nr_seq_cliente_p;
elsif (ie_tipo_texto_p = 8) then
	select 	count(*)
	into STRICT	qtd_w
	from 	med_atestado
	where 	to_date(clock_timestamp(),'dd/mm/yyyy') 	= to_date(dt_atestado,'dd/mm/yyyy')
	and	nr_seq_cliente 			= nr_seq_cliente_p;
elsif (ie_tipo_texto_p = 10) then
	select 	count(*)
	into STRICT	qtd_w
	from 	med_parecer_medico
	where 	to_date(clock_timestamp(),'dd/mm/yyyy') 	= to_date(dt_parecer,'dd/mm/yyyy')
	and	nr_seq_cliente 			= nr_seq_cliente_p;
end if;

if (qtd_w = 0) then
	retorno_w := 'S';
end if;
return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
