SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mhr_get_file_patch ( nr_seq_mhr_doc_meta_p bigint, ds_file_p text) RETURNS varchar AS $body$
DECLARE

ds_patch_w	MHR_DOC_FILES.DS_DOC_FILE_PATH%type;
ds_file_w	MHR_DOC_FILES.DS_DOC_FILE_NAME%type;
pos_w		bigint;

BEGIN

ds_file_w	:= ds_file_p;
pos_w		:= position('#' in ds_file_w);

while pos_w	> 0 loop
	begin
	ds_file_w	:= substr(ds_file_w,pos_w +1);
	
	pos_w		:= position('#' in ds_file_w);
	end;
end loop;

select	max(DS_DOC_FILE_PATH)
into STRICT	ds_patch_w
from	MHR_DOC_FILES
where	nr_seq_mhr_doc_meta	= nr_seq_mhr_doc_meta_p
and	DS_DOC_FILE_NAME	= ds_file_w;


return	ds_patch_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
