SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ml_obter_pend_impressao ( nr_seq_carta_med_p carta_medica.nr_sequencia%type, nr_seq_carta_mae_p carta_medica.nr_seq_carta_mae%type) RETURNS varchar AS $body$
DECLARE

    ie_assinatura_pendente_w    varchar(1);


BEGIN
    select  coalesce(max('S'), 'N') ie_pendente_assinatura
    into STRICT    ie_assinatura_pendente_w
    from    carta_medica cm
    where   nr_sequencia         = nr_seq_carta_med_p
    and     ie_status_aprovacao in ('EE', 'UA', 'AP')
    and     not exists (SELECT  1
                        from    carta_impressao ci
                        where   ((ci.nr_seq_carta_mae = nr_seq_carta_mae_p)
                        or (ci.nr_seq_carta_mae  = nr_seq_carta_med_p)));

    return ie_assinatura_pendente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
