CREATE OR REPLACE FUNCTION modelo_vlr ( nr_seq_declaracao_p bigint, nr_seq_pergunta_p bigint, nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w	varchar(4000);
dt_resultado_w	timestamp;
vl_resultado_w	double precision;


BEGIN

if (nm_tabela_p = 'QUA_EVENTO_RESPOSTA') then
	select	ds_resultado,
		dt_resultado,
		vl_resultado
	into STRICT	ds_resultado_w,
		dt_resultado_w,
		vl_resultado_w
	from	qua_evento_resposta
	where	nr_seq_evento = nr_seq_declaracao_p
	and	nr_seq_pergunta = nr_seq_pergunta_p;
elsif (nm_tabela_p = 'PLS_DECLARACAO_RESPOSTA') then
	select	ds_resultado,
		dt_resultado,
		vl_resultado
	into STRICT	ds_resultado_w,
		dt_resultado_w,
		vl_resultado_w
	from	pls_declaracao_resposta
	where	nr_seq_declaracao = nr_seq_declaracao_p
	and	nr_seq_pergunta = nr_seq_pergunta_p;
end	if;

if (ds_resultado_w IS NOT NULL AND ds_resultado_w::text <> '') then
	return	ds_resultado_w;
elsif (vl_resultado_w IS NOT NULL AND vl_resultado_w::text <> '') then
	return	vl_resultado_w;
elsif (dt_resultado_w IS NOT NULL AND dt_resultado_w::text <> '') then
	return	dt_resultado_w;
else
	return	'';
end	if;

end;
