SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mostrar_informacoes_dum (NR_SEQ_AGENDA_P bigint, IE_INFORMACAO_P text) RETURNS varchar AS $body$
DECLARE


nr_seq_agenda_int_w			agenda_integrada_item.nr_seq_agenda_int%type;
DT_ULTIMA_MENSTRUACAO_W		agenda_integrada.DT_ULTIMA_MENSTRUACAO%type;
QT_IG_SEMANA_W				agenda_integrada.QT_IG_SEMANA%type;
QT_IG_DIA_W					agenda_integrada.QT_IG_DIA%type;
ds_retorno_w				varchar(20) := '';
nm_usuario_w				varchar(20);
cd_estabelecimento_w		estabelecimento.CD_ESTABELECIMENTO%type;
dt_ultima_menstruacao_adic_w		agenda_paciente_inf_adic.DT_ULTIMA_MENSTRUACAO%type;


BEGIN

nm_usuario_w	:= wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

select 	max(nr_seq_agenda_int)
into STRICT 	nr_seq_agenda_int_w 	
from 	agenda_integrada_item
where 	nr_seq_agenda_exame = NR_SEQ_AGENDA_P;

select	max(dt_ultima_menstruacao)
into STRICT	dt_ultima_menstruacao_adic_w
from	agenda_paciente_inf_adic
where	nr_seq_agenda = NR_SEQ_AGENDA_P;

if (nr_seq_agenda_int_w > 0) then
	select 	max(DT_ULTIMA_MENSTRUACAO),
        	max(QT_IG_SEMANA),
            max(QT_IG_DIA)
	into STRICT	DT_ULTIMA_MENSTRUACAO_W,
			QT_IG_SEMANA_W, 
			QT_IG_DIA_W
	from 	agenda_integrada
	where	nr_Sequencia = nr_seq_agenda_int_w;
end if;

if (IE_INFORMACAO_P = 'M') then

	ds_retorno_w :=	to_char(coalesce(dt_ultima_menstruacao_adic_w,DT_ULTIMA_MENSTRUACAO_W),'dd/mm/yyyy');

elsif (IE_INFORMACAO_P = 'S') then
	
	select	CASE WHEN coalesce(coalesce(dt_ultima_menstruacao_adic_w,DT_ULTIMA_MENSTRUACAO_W)::text, '') = '' THEN  QT_IG_SEMANA_W  ELSE max(trunc((trunc(hr_inicio) - trunc(coalesce(dt_ultima_menstruacao_adic_w,DT_ULTIMA_MENSTRUACAO_W)))/7)) END
	into STRICT	ds_retorno_w
	from	agenda_paciente
	where	nr_sequencia = NR_SEQ_AGENDA_P;

elsif (IE_INFORMACAO_P = 'D') then
	
	select	CASE WHEN coalesce(coalesce(dt_ultima_menstruacao_adic_w,DT_ULTIMA_MENSTRUACAO_W)::text, '') = '' THEN  QT_IG_DIA_W  ELSE max(mod((trunc(hr_inicio) - trunc(coalesce(dt_ultima_menstruacao_adic_w,DT_ULTIMA_MENSTRUACAO_W))),7)) END
	into STRICT	ds_retorno_w
	from	agenda_paciente
	where	nr_sequencia = NR_SEQ_AGENDA_P;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION mostrar_informacoes_dum (NR_SEQ_AGENDA_P bigint, IE_INFORMACAO_P text) FROM PUBLIC;

