CREATE OR REPLACE FUNCTION mprev_consistir_inc_part_turma ( nr_seq_turma_p mprev_grupo_col_turma.nr_sequencia%type, nr_seq_grupo_turm_partic_p MPREV_GRUPO_TURMA_PARTIC.nr_sequencia%type, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_max_participante_w		mprev_grupo_col_turma.qt_max_participante%type;
qt_registros_w			bigint;


BEGIN

select	max(qt_max_participante)
into STRICT	qt_max_participante_w
from	mprev_grupo_col_turma
where	nr_sequencia	= nr_seq_turma_p;

if (coalesce(qt_max_participante_w,0) <> 0) then
	select	count(1)
	into STRICT	qt_registros_w
	from	MPREV_GRUPO_TURMA_PARTIC
	where	nr_seq_turma	= nr_seq_turma_p
	and	nr_sequencia	<> coalesce(nr_seq_grupo_turm_partic_p,-1)
	and	((coalesce(dt_saida::text, '') = '') or (dt_saida > dt_referencia_p));

	if (qt_registros_w >= qt_max_participante_w) then
		return	substr(wheb_mensagem_pck.get_texto(290924,'NM_TURMA=' || substr(mprev_obter_nm_turma(nr_seq_turma_p),1,255)||
								';QT_MAXIMA='||qt_max_participante_w||';QT_ATUAL='||qt_registros_w),1,255);
	end if;

end if;

return	'';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
