CREATE OR REPLACE FUNCTION mprev_desc_motivo_susp (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_motivo_w		mprev_motivo_susp_atend.ds_motivo%type;
nr_seq_participante_w	mprev_participante.nr_sequencia%type;
nr_seq_suspensao_w	mprev_suspensao_atend.nr_sequencia%type;
nr_seq_motivo_w		mprev_suspensao_atend.nr_seq_motivo%type;
ie_situacao_w		mprev_participante.ie_situacao%type;


BEGIN
/* Procurar qual a última suspensão ativa para o participante */

select 	max(nr_sequencia)
into STRICT	nr_seq_participante_w
from 	mprev_participante
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (nr_seq_participante_w IS NOT NULL AND nr_seq_participante_w::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_suspensao_w
	from 	mprev_suspensao_atend a
	where 	nr_seq_participante = nr_seq_participante_w
	and	clock_timestamp() between a.dt_inicio and coalesce(a.dt_termino,clock_timestamp());

	if (nr_seq_suspensao_w IS NOT NULL AND nr_seq_suspensao_w::text <> '') then
		select	nr_seq_motivo
		into STRICT	nr_seq_motivo_w
		from 	mprev_suspensao_atend
		where 	nr_sequencia = nr_seq_suspensao_w;

		if (nr_seq_motivo_w IS NOT NULL AND nr_seq_motivo_w::text <> '') then
			select	'(' || ds_motivo || ')'
			into STRICT	ds_motivo_w
			from 	mprev_motivo_susp_atend
			where	nr_sequencia = nr_seq_motivo_w;
		end if;
	end if;
end if;

return	ds_motivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
