CREATE OR REPLACE FUNCTION mprev_obter_dados_agenda (cd_agenda_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w	varchar(255);


BEGIN 
 
 
 
if (ie_opcao_p = 'NM') then 
 
begin 
	select	substr(coalesce(ds_curta, coalesce(ds_agenda, obter_nome_pf(cd_pessoa_fisica))),1,255) 
	into STRICT	ds_retorno_w 
	from	agenda 
	where	cd_agenda = cd_agenda_p;
exception 
when others then 
	ds_retorno_w := null;
end;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
