CREATE OR REPLACE FUNCTION mprev_obter_dados_ativ_plano ( nr_seq_atividade_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Obter dados da atividade prevista do plano de atendimento da Medicina Preventiva.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

IE_OPCAO_P
NM -  Nome da atividade

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_atividade_p IS NOT NULL AND nr_seq_atividade_p::text <> '') then

	if (upper(ie_opcao_p) = 'NM') then
		select	a.nm_atividade
		into STRICT	ds_retorno_w
		from 	mprev_atividade_plano a
		where 	a.nr_sequencia = nr_seq_atividade_p;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
