CREATE OR REPLACE FUNCTION mprev_obter_dados_aval_plano ( nr_seq_ativ_plano_aval_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Obter dados da avalição da atividade prevista do plano de atendimento da Medicina Preventiva.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

IE_OPCAO_P
AV -  Avaliação (Retorna Escala ou Modelo ou Avaliação)

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_ativ_plano_aval_p IS NOT NULL AND nr_seq_ativ_plano_aval_p::text <> '') then

	if (upper(ie_opcao_p) = 'AV') then

		select 	substr(max(b.ds_tipo),1,255)
		into STRICT	ds_retorno_w
		from 	mprev_ativ_plano_aval a,
			med_tipo_avaliacao b
		where	a.nr_seq_tipo_avaliacao = b.nr_sequencia
		and	a.nr_sequencia = nr_seq_ativ_plano_aval_p;

		if (coalesce(ds_retorno_w::text, '') = '') then
			select	substr(max(b.ds_modelo),1,255)
			into STRICT	ds_retorno_w
			from	mprev_ativ_plano_aval a,
				modelo b
			where	a.nr_seq_modelo = b.nr_sequencia
			and	a.nr_sequencia = nr_seq_ativ_plano_aval_p;
		end if;

		if (coalesce(ds_retorno_w::text, '') = '') then
			select	substr(obter_valor_dominio(1799,a.ie_escala),1,255)
			into STRICT	ds_retorno_w
			from	mprev_ativ_plano_aval a
			where	a.nr_sequencia = nr_seq_ativ_plano_aval_p;
		end if;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
