SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mprev_obter_dados_equipe (nr_seq_equipe_p bigint, dt_referencia_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar diferentes informações com relação a equipe da medicina preventiva.
Usada em campos function do dicionario de dados.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: MedPrev - Programas de Promoção a Saúde
[ x ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);
ds_funcoes_w	varchar(255);

/*
ie_opcao_p
'QT' = retorna a quantidade de profissionais
'NM' = Nome da equipe
'FN' = Funções dos profissionais
*/
C01 CURSOR FOR
	SELECT 	distinct
		b.nm_funcao nm_funcao
	from 	mprev_equipe_profissional a,
		mprev_funcao_colaborador b,
		mprev_equipe c
	where 	a.nr_seq_funcao   = b.nr_sequencia
	and 	a.nr_seq_equipe   = c.nr_sequencia
	and	c.nr_sequencia    = nr_seq_equipe_p;



BEGIN

if (upper(ie_opcao_p) = 'QT')then

	if (nr_seq_equipe_p IS NOT NULL AND nr_seq_equipe_p::text <> '') then
		select count(nr_sequencia)
		into STRICT ds_retorno_w
		from mprev_equipe_profissional
		where nr_seq_equipe = nr_seq_equipe_p
		and dt_referencia_p between dt_inclusao and coalesce(dt_exclusao, dt_referencia_p);
	end if;

elsif (upper(ie_opcao_p) = 'NM')then

	if (nr_seq_equipe_p IS NOT NULL AND nr_seq_equipe_p::text <> '') then
		select	nm_equipe
		into STRICT	ds_retorno_w
		from	mprev_equipe
		where	nr_sequencia = nr_seq_equipe_p;
	end if;

elsif (upper(ie_opcao_p) = 'FN')then

	open C01;
	loop
	fetch C01 into
		ds_funcoes_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
				ds_retorno_w := substr(ds_retorno_w || ', ' || ds_funcoes_w,1,255);
				exit when length(ds_retorno_w) >= 254;
			elsif (coalesce(ds_retorno_w::text, '') = '') then
				ds_retorno_w := ds_funcoes_w;
			end if;
		end;
	end loop;
	close C01;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION mprev_obter_dados_equipe (nr_seq_equipe_p bigint, dt_referencia_p timestamp, ie_opcao_p text) FROM PUBLIC;

