CREATE OR REPLACE FUNCTION mprev_obter_dados_exa_plano ( nr_seq_ativ_plano_exame_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Obter dados do exame da atividade prevista do plano de atendimento da Medicina Preventiva.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

IE_OPCAO_P
EX -  Exame

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_ativ_plano_exame_p IS NOT NULL AND nr_seq_ativ_plano_exame_p::text <> '') then

	if (upper(ie_opcao_p) = 'EX') then
		select	max(b.nm_exame)
		into STRICT	ds_retorno_w
		from 	mprev_ativ_plano_exame a,
			exame_laboratorio b
		where 	b.nr_seq_exame = a.nr_seq_exame_lab
		and	a.nr_sequencia = nr_seq_ativ_plano_exame_p;

		if (coalesce(ds_retorno_w::text, '') = '') then
			select	a.nm_outro_exame
			into STRICT	ds_retorno_w
			from	mprev_ativ_plano_exame a
			where	a.nr_sequencia	= nr_seq_ativ_plano_exame_p;
		end if;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
