SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mprev_obter_dados_funcao (nr_seq_funcao_p bigint, dt_referencia_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar diferentes informações com relação a função da medicina preventiva.
Usada em campos function do dicionario de dados.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: MedPrev - Programas de Promoção a Saúde
[ x ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);
ds_funcoes_w	varchar(255);
ds_prof_w	varchar(2000);

/*
ie_opcao_p
'EQ' = retorna a quantidade de equipes com a função
'NM' = Nome da funcao
*/
BEGIN

if (upper(ie_opcao_p) = 'EQ')then

	if (nr_seq_funcao_p IS NOT NULL AND nr_seq_funcao_p::text <> '') then
		select	count(distinct y.nm_equipe)
		into STRICT	ds_retorno_w
		from	mprev_equipe y,
			mprev_equipe_profissional x
		where	x.nr_seq_equipe	= y.nr_sequencia
		and	x.nr_seq_funcao = nr_seq_funcao_p;

	end if;

elsif (upper(ie_opcao_p) = 'NM')then

	if (nr_seq_funcao_p IS NOT NULL AND nr_seq_funcao_p::text <> '') then
		select	nm_funcao
		into STRICT	ds_retorno_w
		from	mprev_funcao_colaborador a
		where	a.nr_sequencia = nr_seq_funcao_p;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
