CREATE OR REPLACE FUNCTION mprev_obter_dados_grupo_turma ( nr_seq_turma_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar diferentes informações da turma de um grupo de atendimento da medicina preventiva.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ie_opcao_p:
'N' - Nome da turma
'NG' - Nome do grupo coletivo
'NNG' - Nome do grupo coletivo ||' - '|| Nome da turma
'ST' - Situação da turma. ( Vigente na data atual ).
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w 		varchar(4000);


BEGIN
if (nr_seq_turma_p IS NOT NULL AND nr_seq_turma_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	if (ie_opcao_p = 'N') then
		select	substr(a.nm_turma, 1, 255)
		into STRICT	ds_retorno_w
		from	mprev_grupo_col_turma a
		where	a.nr_sequencia = nr_seq_turma_p;
	elsif (ie_opcao_p = 'NG') then
		select	substr(mprev_obter_dados_grupo_atend(a.nr_seq_grupo_coletivo, 'NM'), 1, 255)
		into STRICT	ds_retorno_w
		from	mprev_grupo_col_turma a
		where	a.nr_sequencia = nr_seq_turma_p;
	elsif (ie_opcao_p = 'NNG') then
		select substr(mprev_obter_dados_grupo_atend(a.nr_seq_grupo_coletivo, 'NM') ||' - '|| a.nm_turma,1,255)
		into STRICT	ds_retorno_w
		from 	mprev_grupo_col_turma a
		where	a.nr_sequencia = nr_seq_turma_p;
	elsif (ie_opcao_p = 'ST') then
		select	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	mprev_grupo_col_turma a
		where	a.nr_sequencia = nr_seq_turma_p
		and	clock_timestamp() between coalesce(a.dt_inicio,clock_timestamp()) and coalesce(a.dt_termino,clock_timestamp());
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
