CREATE OR REPLACE FUNCTION mprev_obter_dados_lista_espera ( nr_seq_lista_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar diferentes informações da lista de espera da medicina preventiva.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

ie_opcao_p :
'DP' = Descrição da Prioridade de atendimento
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);
ie_prioridade_w	mprev_grupo_turma_espera.ie_prioridade%type;


BEGIN

if (upper(ie_opcao_p) = 'DP')then
	select	coalesce(ie_prioridade,1)
	into STRICT	ie_prioridade_w
	from	mprev_grupo_turma_espera
	where	nr_sequencia = nr_seq_lista_p;

	if (ie_prioridade_w = 1) then
		--Normal
		ds_retorno_w	:= substr(obter_texto_dic_objeto(345383, wheb_usuario_pck.get_nr_seq_idioma, null),1,255);
	else
		--Urgente
		ds_retorno_w	:= substr(obter_texto_dic_objeto(345384, wheb_usuario_pck.get_nr_seq_idioma, null),1,255);
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
