SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mprev_obter_dados_motivo_saida (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Retorna dados da tabela mprev_motivo_saida_grupo.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ie_opcao_p
MS - Motivo de saída do grupo ou turma
TS - Descrição do Tipo de saída do grupo ou turma
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_opcao_w	varchar(2);
ds_retorno_w	varchar(255);


BEGIN

ie_opcao_w	:= upper(ie_opcao_p);

if (nr_sequencia_p is  not null) then

	if (ie_opcao_w = 'TS') then
		select	ie_tipo_saida
		into STRICT	ds_retorno_w
		from	mprev_motivo_saida_grupo
		where	nr_sequencia = nr_sequencia_p;

		if (ds_retorno_w = 'G') then
		--Saída de grupo
			ds_retorno_w	:= substr(obter_texto_dic_objeto(352316,wheb_usuario_pck.get_nr_seq_idioma,null),1,255);
		elsif (ds_retorno_w = 'T') then
		--Saída de turma
			ds_retorno_w	:= substr(obter_texto_dic_objeto(352315,wheb_usuario_pck.get_nr_seq_idioma,null),1,255);
		end if;
	elsif (ie_opcao_w = 'MS') then
		select	substr(ds_motivo,1,255)
		into STRICT	ds_retorno_w
		from	mprev_motivo_saida_grupo
		where	nr_sequencia = nr_sequencia_p;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
