CREATE OR REPLACE FUNCTION mprev_obter_dados_rec_agenda ( nr_seq_recurso_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Buscar dados da tabela mprev_recurso_agenda
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X] Objetos do dicionário [ X] Tasy (Delphi/Java) [] Portal [X]  Relatórios [X] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-----  IE_OPCAO_P ----
TR - Tipo de recurso

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);
ie_opcao_w	varchar(2);


BEGIN

ie_opcao_w	:= upper(ie_opcao_p);

if (ie_opcao_w = 'TR') then
	select	mprev_obter_ds_tipo_recurso(coalesce(a.ie_tipo_recurso,''))
	into STRICT	ds_retorno_w
	from	mprev_recurso_agenda a
	where 	nr_sequencia = nr_seq_recurso_p;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
