CREATE OR REPLACE FUNCTION mprev_obter_dados_regra_dias ( nr_sequencia_p mprev_dias_data_prevista.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Buscar informações da regras cadastrada na HDM - Cadastros gerais \ Regra dia data prevista
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
D - Quantidade de dias
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);
ie_opcao_w	varchar(2);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

	ie_opcao_w := upper(ie_opcao_p);

	if (ie_opcao_w = 'D') then
		select	a.qt_dias
		into STRICT	ds_retorno_w
		from	mprev_dias_data_prevista a
		where	a.nr_sequencia = nr_sequencia_p;
	end if;
end if;

return	substr(ds_retorno_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
