CREATE OR REPLACE FUNCTION mprev_obter_data_fim_ciclo ( ie_tipo_periodo_ciclo_p text, dt_inicio_ciclo_p timestamp, qt_total_periodo_ciclo_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_fim_ciclo_w	timestamp;
dt_retorno_w 	timestamp;


BEGIN

if (ie_tipo_periodo_ciclo_p IS NOT NULL AND ie_tipo_periodo_ciclo_p::text <> '') then
		
	if ( ie_tipo_periodo_ciclo_p = 'A') then
		dt_fim_ciclo_w	:=	pkg_date_utils.add_month(coalesce(dt_inicio_ciclo_p, clock_timestamp()), 12 * coalesce(qt_total_periodo_ciclo_p,0), 0);
	elsif (ie_tipo_periodo_ciclo_p = 'M') then
		dt_fim_ciclo_w	:=	pkg_date_utils.add_month(coalesce(dt_inicio_ciclo_p, clock_timestamp()), 1 * coalesce(qt_total_periodo_ciclo_p,0), 0);
	elsif (ie_tipo_periodo_ciclo_p = 'S') then
		dt_fim_ciclo_w	:=	coalesce(dt_inicio_ciclo_p, clock_timestamp()) + (7 * coalesce(qt_total_periodo_ciclo_p,0));
	elsif (ie_tipo_periodo_ciclo_p = 'D') then
		dt_fim_ciclo_w	:=	coalesce(dt_inicio_ciclo_p, clock_timestamp()) + (1 * coalesce(qt_total_periodo_ciclo_p,0));
	end if;
	
end if;

dt_retorno_w	:= obter_proximo_dia_util(wheb_usuario_pck.get_cd_estabelecimento,dt_fim_ciclo_w);

return	dt_retorno_w;

end;
$body$
