CREATE OR REPLACE FUNCTION mprev_obter_data_prev_ciclo ( ie_tipo_intervalo_p text, dt_inicio_ciclo_p timestamp, qt_intervalo_anterior_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_prevista_w	timestamp;
dt_retorno_w timestamp;


BEGIN

if (ie_tipo_intervalo_p IS NOT NULL AND ie_tipo_intervalo_p::text <> '') then

	if (ie_tipo_intervalo_p = 'M') then
		dt_prevista_w	:= pkg_date_utils.add_month( coalesce(dt_inicio_ciclo_p, clock_timestamp()), coalesce(qt_intervalo_anterior_p, 0),0);
	elsif (ie_tipo_intervalo_p = 'A') then
		dt_prevista_w	:= pkg_date_utils.add_month( coalesce(dt_inicio_ciclo_p, clock_timestamp()),(12*coalesce(qt_intervalo_anterior_p, 0)),0);
	elsif (ie_tipo_intervalo_p = 'D') then
		dt_prevista_w	:= coalesce(dt_inicio_ciclo_p, clock_timestamp()) + coalesce(qt_intervalo_anterior_p, 0);
	elsif (ie_tipo_intervalo_p = 'S') then
		dt_prevista_w	:= coalesce(dt_inicio_ciclo_p, clock_timestamp()) + (coalesce(qt_intervalo_anterior_p, 0) * 7);
	end if;

end if;

dt_retorno_w	:= obter_proximo_dia_util(wheb_usuario_pck.get_cd_estabelecimento, dt_prevista_w);

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
