SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mprev_obter_desc_busca (nr_sequencia_p bigint, tabela_de_busca_p text) RETURNS varchar AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar a descrição da opçao de Procurar por (Busca) , se null tratar como Conta Médica.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: MedPrev - Programas de Promoção a Saúde
[ x ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
(CO, Conta médica OPS
AO, Autorização OPS)

A Consulta pode ser para a tabela 'MPREV_REGRA_CUBO_PROC'  e para a tabela MPREV_REGRA_CUBO_ATEND
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);
ie_busca_w      varchar(255);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	if tabela_de_busca_p = 'MPREV_REGRA_CUBO_ATEND' then
		select upper(ie_busca)
		into STRICT ie_busca_w
		from MPREV_REGRA_CUBO_ATEND
		where nr_sequencia = nr_sequencia_p;
	else
		select upper(ie_busca)
		into STRICT ie_busca_w
		from MPREV_REGRA_CUBO_PROC
		where nr_sequencia = nr_sequencia_p;
	end if;

	if ((coalesce(ie_busca_w::text, '') = '') or (ie_busca_w = 'CO')) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(795695);
	else
		ds_retorno_w := wheb_mensagem_pck.get_texto(795696);
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
