SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mprev_obter_desc_diagnostico (nr_seq_diagnostico_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar a descrição da patologia ou do fator de risco de acordo com o código passado e a opção
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: MedPrev - Programas de Promoção a Saúde
[ x ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
/*
IE_OPCAO_P
DS	- Descrição
TP	- Tipo
*/
ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_diagnostico_p IS NOT NULL AND nr_seq_diagnostico_p::text <> '') then

	if (upper(ie_opcao_p) = 'TP') then
    --295349 = Patologia / 309096 = Fator de Risco / 308326 = Ambos
		select	CASE WHEN ie_utilizacao='DI' THEN  obter_desc_expressao(295349) WHEN ie_utilizacao='FR' THEN  obter_desc_expressao(309096)  ELSE obter_desc_expressao(308326) END
		into STRICT	ds_retorno_w
		from	diagnostico_interno
		where	nr_sequencia = nr_seq_diagnostico_p;

	elsif (upper(ie_opcao_p) = 'DS') then
		select 	ds_diagnostico
		into STRICT	ds_retorno_w
		from 	diagnostico_interno
		where	nr_sequencia = nr_seq_diagnostico_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
