CREATE OR REPLACE FUNCTION mprev_obter_desc_tema (nr_seq_tema_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Buscar descrição do tema cadastrado na tabela mprev_tema_programa da função MedPrev - Cadastros gerais
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [X] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_tema_w	varchar(255);


BEGIN

if (nr_seq_tema_p IS NOT NULL AND nr_seq_tema_p::text <> '')then
	select 	ds_tema
	into STRICT    	ds_tema_w
	from 	mprev_tema_programa
	where 	nr_sequencia = nr_seq_tema_p;
end if;
return	ds_tema_w;

end;
$body$
