CREATE OR REPLACE FUNCTION mprev_obter_ds_recurso ( nr_sequencia_p mprev_recurso_agenda.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  
-------------------------------------------------------------------------------------------------------------------
