ds_retorno_w	varchar(255);


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then	
	select	substr(a.nm_recurso,1,255)
	into STRICT	ds_retorno_w
	from	mprev_recurso_agenda a
	where	nr_sequencia = nr_sequencia_p;
	
end if;

return	ds_retorno_w;

end;
