CREATE OR REPLACE FUNCTION mprev_obter_ds_tipo_recurso ( ie_tipo_recurso_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X] Objetos do dicionário [ X] Tasy (Delphi/Java) [] Portal [X]  Relatórios [X] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);


BEGIN
if (ie_tipo_recurso_p IS NOT NULL AND ie_tipo_recurso_p::text <> '') then
	if (ie_tipo_recurso_p = 'G') then
		--Genérico
		ds_retorno_w	:= substr(obter_texto_dic_objeto(344686, wheb_usuario_pck.get_nr_seq_idioma, null),1,255);
	elsif (ie_tipo_recurso_p = 'V') then
		--Veículo
		ds_retorno_w	:= substr(obter_texto_dic_objeto(344689, wheb_usuario_pck.get_nr_seq_idioma, null),1,255);
	elsif (ie_tipo_recurso_p = 'E') then
		--Equipamento
		ds_retorno_w	:= substr(obter_texto_dic_objeto(344690, wheb_usuario_pck.get_nr_seq_idioma, null),1,255);
	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
