SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mprev_obter_dt_inclusao_indic (nr_seq_indicacao_p bigint) RETURNS timestamp AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar qual o dia que a pessoa indicada foi inclusa no programa ou campanha destino, no caso
de sucesso.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
dt_inclusao_w	timestamp	:= null;


BEGIN

begin
select	min(y.dt_inclusao)
into STRICT	dt_inclusao_w
from	mprev_programa_partic y,
		mprev_captacao_destino d,
		mprev_captacao c,
		mprev_participante b
where	y.nr_seq_programa = d.nr_seq_programa
and		c.cd_pessoa_fisica	= b.cd_pessoa_fisica
and		b.nr_sequencia	= y.nr_seq_participante
and		c.dt_inclusao <= y.dt_inclusao
and		c.nr_sequencia	= d.nr_seq_captacao
and		c.nr_seq_indicacao = nr_seq_indicacao_p;
exception
	when others then
	dt_inclusao_w	:= null;
end;
/* Se não achou por programa procura por campanha */

if (coalesce(dt_inclusao_w::text, '') = '') then
	begin
	select	min(y.dt_inclusao)
	into STRICT	dt_inclusao_w
	from	mprev_campanha_partic y,
			mprev_captacao_destino d,
			mprev_captacao c,
			mprev_participante b
	where	y.nr_seq_campanha = d.nr_seq_campanha
	and		c.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and		b.nr_sequencia	= y.nr_seq_participante
	and		c.dt_inclusao <= y.dt_inclusao
	and		c.nr_sequencia	= d.nr_seq_captacao
	and		c.nr_seq_indicacao = nr_seq_indicacao_p;
	exception
		when others then
		dt_inclusao_w	:= null;
	end;
end if;

return	dt_inclusao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION mprev_obter_dt_inclusao_indic (nr_seq_indicacao_p bigint) FROM PUBLIC;

