CREATE OR REPLACE FUNCTION mprev_obter_func_funcao (nr_seq_funcao_p bigint) RETURNS bigint AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar a quantidade de funcionarios ligados a funcao
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionario [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_retorno_w	 integer;


BEGIN
	select count(distinct cd_pessoa_fisica)
	into STRICT qt_retorno_w
	from mprev_equipe_profissional
	where nr_seq_funcao = nr_seq_funcao_p
	and clock_timestamp() between dt_inclusao and coalesce(dt_exclusao, clock_timestamp());
	
return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
