CREATE OR REPLACE FUNCTION mprev_obter_nm_participante (nr_seq_participante_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter o nome do participante
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:  MedPrev - Programas de Promoção da Saúde.
[  x]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nm_participante_w	varchar(255);
cd_pessoa_fisica_w      varchar(10);


BEGIN

if (nr_seq_participante_p IS NOT NULL AND nr_seq_participante_p::text <> '')then

	select 	cd_pessoa_fisica
	into STRICT	cd_pessoa_fisica_w
	from 	mprev_participante
	where 	nr_sequencia = nr_seq_participante_p;

	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '')then

		nm_participante_w := substr(obter_nome_pessoa_fisica(cd_pessoa_fisica_w,null),1,255);

	end if;

end if;

return	nm_participante_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
