CREATE OR REPLACE FUNCTION mprev_obter_nome_agenda (cd_agenda_p agenda.cd_agenda%type) RETURNS varchar AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Retornar o nome das agendas da Medicina Preventiva, 
tratamento em nome curto e longo, nome do profissional 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ X ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
nm_agenda_w	varchar(255)	:= null;


BEGIN 
 
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') then 
	select	coalesce(ds_curta,ds_agenda) 
	into STRICT	nm_agenda_w 
	from	agenda 
	where	cd_agenda	= cd_agenda_p;
end if;
 
return	nm_agenda_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
