CREATE OR REPLACE FUNCTION mprev_obter_prof_resp_programa ( nr_seq_programa_partic_p bigint) RETURNS varchar AS $body$
DECLARE

 
nm_profissional_w	varchar(255);	
 

BEGIN 
 
select	max(substr(obter_nome_pf(a.cd_profissional), 1, 255)) 
into STRICT	nm_profissional_w 
from	mprev_prog_partic_prof a 
where	a.nr_seq_programa_partic = nr_seq_programa_partic_p 
and	(a.cd_profissional IS NOT NULL AND a.cd_profissional::text <> '') 
and	clock_timestamp() between a.dt_inicio_acomp and coalesce(a.dt_fim_acomp, clock_timestamp() + interval '1 days')	 
order by 1;
 
return nm_profissional_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
