CREATE OR REPLACE FUNCTION mprev_obter_regra_evento_atend ( nr_seq_mprev_atend_pac_p mprev_atend_paciente.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_w			bigint;
ie_individual_coletivo_w	mprev_atendimento.ie_individual_coletivo%type;
ie_forma_atendimento_w		mprev_atendimento.ie_forma_atendimento%type;
nr_seq_funcao_prof_w		mprev_equipe_profissional.nr_seq_funcao%type;
nr_seq_grupo_coletivo_w		mprev_grupo_col_turma.nr_seq_grupo_coletivo%type;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	mprev_regra_evento_atend
	where (ie_tipo_atendimento_mprev = ie_individual_coletivo_w or coalesce(ie_tipo_atendimento_mprev::text, '') = '')
	and (ie_forma_atendimento_mprev = ie_forma_atendimento_w or coalesce(ie_forma_atendimento_mprev::text, '') = '')
	and (nr_seq_funcao_prof = nr_seq_funcao_prof_w or coalesce(nr_seq_funcao_prof::text, '') = '')
	and (nr_seq_grupo_col = nr_seq_grupo_coletivo_w or coalesce(nr_seq_grupo_col::text, '') = '');


BEGIN

select	b.ie_individual_coletivo,
	b.ie_forma_atendimento,
	(select max(nr_seq_funcao) from mprev_equipe_profissional where cd_pessoa_fisica = b.cd_profissional) nr_seq_funcao,
	(select max(nr_seq_grupo_coletivo) from mprev_grupo_col_turma where nr_sequencia = b.nr_seq_turma) nr_seq_grupo_coletivo
into STRICT	ie_individual_coletivo_w,
	ie_forma_atendimento_w,
	nr_seq_funcao_prof_w,
	nr_seq_grupo_coletivo_w
from	mprev_atend_paciente a,
	mprev_atendimento b
where	a.nr_sequencia = nr_seq_mprev_atend_pac_p
and	b.nr_sequencia = a.nr_seq_atendimento;

open c01;
loop
fetch c01 into
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	null;
	end;
end loop;
close c01;

return	nr_seq_regra_w;

end;
$body$
