CREATE OR REPLACE FUNCTION mprev_obter_resp_ciclo_item ( nr_seq_partic_ciclo_item_p mprev_partic_ciclo_item.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Obter o nome dos profissionais ou funções profissionais responsáveis pelo atendimento 
previsto 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ X] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
 
IE_OPCAO_P 
NP - Nomes profissionais 
NF - Nomes funções 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
ds_retorno_w	varchar(255)	:= null;
nm_prof_w	pessoa_fisica.nm_pessoa_fisica%type;
nm_funcao_w	mprev_funcao_colaborador.nm_funcao%type;

c01 CURSOR FOR 
	SELECT	obter_nome_pf(a.cd_profissional) nm_prof, 
		mprev_obter_desc_funcao(a.nr_seq_funcao_colab) nm_funcao 
	from	mprev_part_cic_item_resp a 
	where	a.nr_seq_partic_ciclo_item = nr_seq_partic_ciclo_item_p;


BEGIN 
open C01;
loop 
fetch C01 into	 
	nm_prof_w, 
	nm_funcao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then 
		ds_retorno_w	:= ds_retorno_w || ', ';
	end if;
	if (ie_opcao_p = 'NP') then 
		ds_retorno_w	:= substr(ds_retorno_w || nm_prof_w,1,255);
	elsif (ie_opcao_p = 'NF') then 
		ds_retorno_w	:= substr(ds_retorno_w || nm_funcao_w,1,255);	
	end if;
	end;
end loop;
close C01;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
