CREATE OR REPLACE FUNCTION mprev_obter_resp_equipe_pf ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Obter o responsável da equipe da pessoa fisica.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_pessoa_responsavel_w	pessoa_fisica.cd_pessoa_fisica%type;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	/*Verifica se a pessoa é responsável por alguma equipe*/

	select	max(a.cd_pessoa_responsavel)
	into STRICT	cd_pessoa_responsavel_w
	from	mprev_equipe a
	where	a.cd_pessoa_responsavel = cd_pessoa_fisica_p;

	if (coalesce(cd_pessoa_responsavel_w::text, '') = '') then
		/*Se a pessoa não for responsável por alguma equipe vai buscar o responsável pela equipe que ele pertence.*/

		select 	max(a.cd_pessoa_responsavel)
		into STRICT	cd_pessoa_responsavel_w
		from	mprev_equipe a,
			mprev_equipe_profissional b
		where	b.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	a.nr_sequencia = b.nr_seq_equipe
		and	coalesce(b.dt_exclusao::text, '') = '';
	end if;
end if;

return	cd_pessoa_responsavel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
