SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mprev_obter_se_indic_incluso (nr_seq_indicacao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(2) := 'N';
qt_registros_w		bigint;


BEGIN

select	count(1)
into STRICT	qt_registros_w
from	mprev_programa_partic y,
	mprev_captacao_destino d,
	mprev_captacao c
where	y.nr_seq_capt_destino = d.nr_sequencia
and	c.nr_sequencia	= d.nr_seq_captacao
and	c.nr_seq_indicacao = nr_seq_indicacao_p;

if ( qt_registros_w = 0 ) then

	select	count(1)
	into STRICT	qt_registros_w
	from	mprev_campanha_partic y,
		mprev_captacao_destino d,
		mprev_captacao c
	where	y.nr_seq_capt_destino = d.nr_sequencia
	and	c.nr_sequencia	= d.nr_seq_captacao
	and	c.nr_seq_indicacao = nr_seq_indicacao_p;

end if;

if (qt_registros_w > 0 ) then
	ie_retorno_w := 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
