CREATE OR REPLACE FUNCTION mprev_obter_se_lista_espera ( nr_seq_participante_p bigint, nr_seq_turma_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[x]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
/* IE_OPCAO_P
G - Grupo
T - Turma
*/
ie_incluso_w	varchar(1);
count_w         bigint;
nr_seq_grupo_w  bigint;


BEGIN
	ie_incluso_w := 'N';

	-- Grupo
	if (UPPER(ie_opcao_p) = 'G') then
		SELECT 	nr_seq_grupo_coletivo
		into STRICT	nr_seq_grupo_w
		FROM	MPREV_GRUPO_COL_TURMA
		WHERE 	nr_sequencia = nr_seq_turma_p;

		select 	count(*)
		  into STRICT	count_w
		  from 	mprev_grupo_turma_espera
		 where 	nr_seq_participante = nr_seq_participante_p
		   and 	nr_seq_grupo = nr_seq_grupo_w;
	end if;

	-- Turma
	if (UPPER(ie_opcao_p) = 'T') then
		select 	count(*)
		  into STRICT  count_w
		  from 	mprev_grupo_turma_espera
		 where 	nr_seq_participante = nr_seq_participante_p
		   and 	nr_seq_turma = nr_seq_turma_p;
	end if;

	if (count_w > 0) then
		ie_incluso_w := 'S';
	end if;

return	ie_incluso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
