CREATE OR REPLACE FUNCTION mprev_obter_se_modulo ( cd_pessoa_fisica_p text, nr_seq_modulo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN
SELECT CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
INTO STRICT ds_retorno_w
FROM mprev_participante a,
     mprev_programa_partic b,
	 MPREV_PROGRAMA_MODULO c,
	 MPREV_PROG_PARTIC_MODULO d
WHERE a.nr_sequencia = b.nr_seq_participante
AND   c.nr_sequencia = d.NR_SEQ_PROG_MODULO
AND   d.NR_SEQ_PROGRAMA_PARTIC = b.nr_sequencia
AND   clock_timestamp() between d.dt_entrada and coalesce(d.dt_saida,clock_timestamp())
AND   c.ie_situacao = 'A'
AND   a.cd_pessoa_fisica = cd_pessoa_fisica_p
AND   c.nr_seq_modulo = nr_seq_modulo_p;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
