CREATE OR REPLACE FUNCTION mprev_obter_se_part_programa (cd_pessoa_fisica_p text, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se a pessoa está participando de um programa da medicina preventiva.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: package mprev_pop_alvo_pck.gerar_populacao_alvo
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w 	varchar(1) 	:= 'N';
qt_partic_w	bigint	:= 0;


BEGIN

	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		begin

		select	count(1)
		into STRICT 	qt_partic_w
		from	mprev_programa_partic b,
			mprev_participante a
		where	a.nr_sequencia = b.nr_seq_participante
		and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and 	dt_referencia_p between b.dt_inclusao and coalesce(b.dt_exclusao, dt_referencia_p);

		exception

		when others then

			qt_partic_w := 0;

		end;

		if ((qt_partic_w IS NOT NULL AND qt_partic_w::text <> '') and qt_partic_w > 0) then
			ds_retorno_w := 'S';
		end if;

	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
