CREATE OR REPLACE FUNCTION mprev_popular_captacao (cd_captacao bigint) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w 		pessoa_fisica.ds_historico%type;

    
BEGIN
    
    select coalesce(substr(obter_valor_dominio(5787,x.ie_origem) || CASE WHEN coalesce(x.nr_sequencia::text, '') = '' THEN null  ELSE ' - ' END  || obter_nome_pf(x.cd_pessoa_fisica),1,255), ' ') into STRICT ds_retorno_w
    from mprev_captacao x 
    where x.nr_sequencia = cd_captacao 
    order by x.nr_sequencia;

    return	ds_retorno_w;
 exception
      when no_data_found then return null;
      when too_many_rows then raise;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
