SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION mprev_valida_ciclo_atendimento (nr_seq_modulo_p bigint, nr_seq_clasificacao_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Validar o Plano de Atendimento,  ao cadastrar o ciclo na função OPS - Cadastrar Gerais / Módulo de atendimento
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  x ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_restringe_w		bigint;
ds_retorno_w		varchar(255)	:= null;
ds_modulo_w		varchar(255);
ds_classif_w		varchar(255);


BEGIN

if (nr_seq_modulo_p IS NOT NULL AND nr_seq_modulo_p::text <> '') then

	select	ds_modulo
	into STRICT	ds_modulo_w
	from	mprev_modulo_atend
	where	nr_sequencia = nr_seq_modulo_p;

	select	substr(obter_ds_classif_plano_atend(nr_seq_clasificacao_p),1,130)
	into STRICT	ds_classif_w
	;

	select 	count(*)
	into STRICT	ie_restringe_w
	from   	mprev_plano_atendimento
	where	nr_seq_modulo = nr_seq_modulo_p
	and	nr_seq_classificacao = nr_seq_clasificacao_p
	and	ie_situacao = 'A';

	if (ie_restringe_w > 0) then
		ds_retorno_w := obter_texto_dic_objeto( 262983 ,wheb_usuario_pck.get_nr_seq_idioma, 'DS_CLASSIF='||ds_classif_w||';DS_MODULO='||ds_modulo_w);
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION mprev_valida_ciclo_atendimento (nr_seq_modulo_p bigint, nr_seq_clasificacao_p bigint) FROM PUBLIC;

