CREATE OR REPLACE FUNCTION mprev_val_camp_acao (nr_seq_campanha_p bigint, nr_seq_acao_p bigint, nr_seq_atual_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(10);
qt_acao_w	bigint;
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar a possibilidade de a ação a ser inserida ser igual a alguma ação ja cadastrada para a campanha selecionada.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
	if (nr_seq_campanha_p IS NOT NULL AND nr_seq_campanha_p::text <> '') and (nr_seq_acao_p IS NOT NULL AND nr_seq_acao_p::text <> '') then
		select	count(1)
		into STRICT	qt_acao_w
		from	mprev_camp_acao
		where	nr_seq_campanha = nr_seq_campanha_p
		and	nr_seq_acao = nr_seq_acao_p
		and	nr_seq_atual_p <> nr_sequencia;

		if (qt_acao_w > 0) then
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(247940);
		else
			ds_retorno_w := 'S';
		end if;
	end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
