CREATE OR REPLACE FUNCTION mprev_val_camp_partic (nr_seq_campanha_p bigint, nr_seq_participante_p bigint, nr_seq_atual_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(10);
qt_equipe_w	bigint;
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar a possibilidade de que o participante a ser inserido seja igual a algum participante ja cadastrado.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
	if (nr_seq_campanha_p IS NOT NULL AND nr_seq_campanha_p::text <> '') and (nr_seq_participante_p IS NOT NULL AND nr_seq_participante_p::text <> '') then
		select	count(1)
		into STRICT	qt_equipe_w
		from	mprev_campanha_partic
		where	nr_seq_campanha = nr_seq_campanha_p
		and	nr_seq_participante = nr_seq_participante_p
		and	nr_seq_atual_p <> nr_sequencia;

		if (qt_equipe_w > 0) then
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(248063);
		else
			ds_retorno_w := 'S';
		end if;
	end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
